import requests
from bs4 import BeautifulSoup
import time
import csv

def get_fighter_details(fighter_name):
    # Construct Wikipedia URL for the fighter
    url = f'https://en.wikipedia.org/wiki/{fighter_name.replace(" ", "_")}'
    
    # Fetch the page
    response = requests.get(url)
    
    if response.status_code != 200:
        print(f"Failed to fetch page for {fighter_name}")
        return None
    
    # Parse the page content
    soup = BeautifulSoup(response.content, 'html.parser')
    
    # Initialize the details dictionary
    details = {}

    # Extract the infobox (which contains most of the general information)
    infobox = soup.find('table', {'class': 'infobox'})

    if infobox:
        # Extract each row of the infobox
        rows = infobox.find_all('tr')
        
        for row in rows:
            # Get the header (label) and data for each row
            header = row.find('th')
            data = row.find('td')
            
            if header and data:
                label = header.get_text(strip=True)
                value = data.get_text(strip=True)
                if label in ['Height', 'Weight', 'Division', 'Years active']:
                    details[label] = value

    # Extract fight history (if available)
    fight_history = []
    
    # Look for tables related to fight history (usually appears under "Fighting Record" or similar heading)
    fight_table = soup.find_all('table', class_='wikitable')[1]
    
    if fight_table:
        fight_rows = fight_table.find_all('tr')[1:]  # Skip the header row
        for row in fight_rows:
            fight_data = row.find_all('td')
            if len(fight_data) >= 8:
                # Example: [Date, Opponent, Result, etc.]
                fight_info = {
                    "res": fight_data[0].get_text(strip=True),
                    "record": fight_data[1].get_text(strip=True),
                    "opponent": fight_data[2].get_text(strip=True),
                    "method": fight_data[3].get_text(strip=True),
                    "event": fight_data[4].get_text(strip=True),
                    "date": fight_data[5].get_text(strip=True),
                    "round": fight_data[6].get_text(strip=True),
                    "time": fight_data[7].get_text(strip=True),
                    "location": fight_data[8].get_text(strip=True),
                }
                fight_history.append(fight_info)
                time.sleep(0.01)
                    
    details['Fight History'] = fight_history

    return details


