# Open CSV file to save data
with open('ufc_fighter_data.csv', mode='w', newline='', encoding='utf-8') as file:
    writer = csv.writer(file)
    
    # Write header with the required columns
    writer.writerow([
        "FighterName", "Infobox Detail", "Values", 
        "FightResult", "Record", "Opponent", "Method", 
        "Event", "Date", "Round", "Time", "Location"
    ])

    for fighter in fighter_names:
        print(f"Fetching details for: {fighter}")
        fighter_details = get_fighter_details(fighter)
        
        if fighter_details:
            print(f"Details for {fighter}:")

            # Save general details first
            infobox_data = []
            for label, value in fighter_details.items():
                if label != 'Fight History':  # Skip fight history here
                    infobox_data.extend([value])  # Each general detail (key-value pair) will go in separate columns

            # Write general info in a single row
            base_row = [fighter] + infobox_data

            # Now, for each fight, write the fight history data
            for fight in fighter_details['Fight History']:
                # Add fight-specific data to the same row
                fight_row = base_row + [
                    fight['res'], fight['record'], fight['opponent'], fight['method'], 
                    fight['event'], fight['date'], fight['round'], fight['time'], fight['location']
                ]
                writer.writerow(fight_row)

            print(f"Data saved for {fighter}\n")